import Expenses from "./components/Expenses/Expenses";
import NewExpense from "./components/NewExpense/NewExpense";
import { useState } from "react";

const DUMMY_EXPENSES = [
  {
    id: "e1",
    title: "Toilet Paper",
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

function App() {
  const [expenses, setExpenses] = useState(DUMMY_EXPENSES);
  const [initYear, setInitYear] = useState(2019);

  const expnseHandler = (expense) => {
    setExpenses((prevState) => {
      const year = [expense, ...prevState].filter((expense) => {
        expense.date.getFullYear() === initYear;
      });
      return year;
    });
  };

  const changeYearFilter = (year) => {
    console.log(year);
  };

  return (
    <div>
      <NewExpense onExpensDataForm={expnseHandler} />
      <Expenses itmes={expenses} onFilterYear={changeYearFilter} />
    </div>
  );
}

export default App;
