import classes from "./AddUser.module.css";
import Card from "../UI/Card";
import Button from "../UI/Button";
import { useState } from "react";

const AddUser = (props) => {
  const [enteredName, setenteredName] = useState("");
  const [enteredNumber, setenteredNumber] = useState("");

  const onNameHanlder = (e) => {
    setenteredName(e.target.value);
    // console.log(e.target.value);
  };

  const onNumbeHandler = (e) => {
    setenteredNumber(e.target.value);
  };

  const submitHandler = (e) => {
    e.preventDefault();

    if (enteredName.trim().length === 0 || enteredNumber.trim().length === 0) {
      alert("Invalid name or number. Please type a valid input.");
      return;
    }

    if (+enteredNumber <= 0) {
      alert("Your age can not be equal to 0 or less than so.");
      return;
    }
    console.log(enteredName, enteredNumber);
    setenteredName("");
    setenteredNumber("");
  };

  return (
    <Card className={classes.input}>
      <form onSubmit={submitHandler}>
        <label htmlFor="InputName"> Name</label>
        <input
          id="InputName"
          type="text"
          value={enteredName}
          onChange={onNameHanlder}
        />
        <label htmlFor="InputAge"> Age</label>
        <input
          id="InputAge"
          type="number"
          value={enteredNumber}
          onChange={onNumbeHandler}
        />

        <Button type="submit">Add user</Button>
      </form>
    </Card>
  );
};

export default AddUser;
